mapper.select:
select

,

from app_c_package where 1=1

  AND name = #{name, jdbcType=VARCHAR}


  AND status = #{status, jdbcType=VARCHAR}


  AND  wj_code = #{wjCode, jdbcType=VARCHAR}


  AND price >= #{min_price, jdbcType=INTEGER}


  AND price <= #{max_price, jdbcType=INTEGER}


  AND sale_num >= #{min_saleNum, jdbcType=INTEGER}


  AND sale_num <= #{max_saleNum, jdbcType=INTEGER}

select

,

from app_c_package where 1=1

  AND name = #{name, jdbcType=VARCHAR}


  AND status = #{status, jdbcType=VARCHAR}


  AND wj_code = #{wjCode, jdbcType=VARCHAR}


  AND price >= #{min_price, jdbcType=INTEGER}


  AND price <= #{max_price, jdbcType=INTEGER}


  AND sale_num >= #{min_saleNum, jdbcType=INTEGER}


  AND sale_num <= #{max_saleNum, jdbcType=INTEGER}


  limit #{start_num, jdbcType=INTEGER}, #{pageSize, jdbcType=INTEGER}
